package com.eme.ims.codec;import org.apache.mina.core.session.IoSession;import org.apache.mina.filter.codec.ProtocolCodecFactory;import org.apache.mina.filter.codec.ProtocolDecoder;import org.apache.mina.filter.codec.ProtocolEncoder;import com.eme.ims.utils.PropertyConfig;public class MessageCodecFactory implements ProtocolCodecFactory{		private ProtocolEncoder encoder;	private ProtocolDecoder decoder;			private PropertyConfig config;		public MessageCodecFactory(PropertyConfig config) {		this.config  = config;		encoder = new MessageEncoder(this.config);		decoder = new MessageDecoder(this.config);	}		@Override	public ProtocolEncoder getEncoder(IoSession session) throws Exception {		return encoder;	}	@Override	public ProtocolDecoder getDecoder(IoSession session) throws Exception {		return decoder;	}}