package com.eme.ims.utils;import java.io.FileInputStream;import java.io.IOException;import java.io.InputStream;import java.util.Properties;import java.util.Set;public class PropertyConfig {		private static final Properties prop = new Properties();		public PropertyConfig(String fileName, int server)  {		try {			if (server==0 ){				String packageName = this.getClass().getPackage().getName();				packageName = packageName.replace(".", "/");				String path = getClass().getResource("").getPath();				path = path.replace(packageName, "");				fileName = path+fileName;			}			prop.load(new FileInputStream(fileName));		} catch (Exception e) {			e.printStackTrace();		}	}		public Set<Object> getKeySet() {		return prop.keySet();	}		public int getKeyCount() {		return this.getKeySet().size();	}		public PropertyConfig(InputStream inputStream) {		try {			prop.load(inputStream);		} catch (IOException e) {			e.printStackTrace();		}	}		public String getString(String key) {		return prop.getProperty(key);	}		public Integer getInteger(String key) {		Integer result = null;				String value = prop.getProperty(key);				if (null != value) {			result = Integer.valueOf(value);		}				return result;	}		}